import {
    REGISTER_SUCCESS,REGISTER_FAIL, 
    USER_LOADED,  AUTH_ERROR,
   LOGIN_SUCCESS,LOGOUT, LOGIN_FAIL} from "../actions/types";

const initialState={
    token:localStorage.getItem("token"),
    isAuthenticated:null,
    loading:true,
    user:null

}

export default function(state=initialState,action){
        const{type,payload}=action;

        switch (type) {
            case USER_LOADED:
                return{
                    ...state,
                    token:null,
                    isAuthenticated:true,
                    loading:false,
                    user:payload

                }

            case REGISTER_SUCCESS:
                case LOGIN_SUCCESS:
                localStorage.setItem("token",payload.token);
                return {
                    ...state,
                    token:null,
                    isAuthenticated:true,
                    loading:true
                }

            case REGISTER_FAIL:
                case AUTH_ERROR:
                    case LOGIN_FAIL:
                        case LOGOUT:

                        localStorage.removeItem("token")

                    return {
                        ...state,
                        token:null,
                        isAuthenticated:false,
                        loading:false
                    };
                    default:
                    return state;


        }

}
