import { Switch } from "react-router";

const initialState={
    profile:null,
    profiles:[],
    repos:[],
    loading:true,
    error:{}
}



export default function(state=initialState,action){
    const {type,payload}=action;

        switch(type){
            case GET_PROFILE:
                return {
                    ...state,
                    profile:payload,
                    loading:false
                };



        }


}
